'use strict';
import * as WEMath from 'WEMath';
//import * as WEVector from 'WEVector';

const prismAsset = engine.registerAsset("models/prism/prism.mdl");
const orbAsset = engine.registerAsset("particles/orb.json");
	// Child particle systems
	engine.registerAsset("orbTrail.json");
	engine.registerAsset("orbGlow.json");

let prisms = [];
const numPrisms = 12;
const prismDist = 8;

let orbs = [];
const numOrbs = 7;
const orbDist = 5;


/**
 * @param {Vec3} value (for property 'origin')
 */
export function init(value) {
	for (let i = 0; i < numPrisms; i++)
	{
		prisms[i] = thisScene.createLayer(prismAsset);
		prisms[i].scale = prisms[i].scale.multiply(0.1);
	}
	for (let i = 0; i < numOrbs; i++)
	{
		orbs[i] = thisScene.createLayer(orbAsset);
		orbs[i].color = new Vec3(1, 0, 0);
	}
	return value;
}

export function update(value) {
	for (let i = 0; i < prisms.length; i++)
	{
		let newAngle = i * 360 / prisms.length - engine.runtime * 2.0;
		let newAngleRad = newAngle * WEMath.deg2rad;
		prisms[i].angles = new Vec3(
			prisms[i].angles.x, 
			prisms[i].angles.y + 15.0 * engine.frametime, 
			newAngle);
		prisms[i].origin = new Vec3(
			-Math.sin(newAngleRad) * prismDist, 
			Math.cos(newAngleRad) * prismDist, 
			0);
	}
	for (let i = 0; i < orbs.length; i++)
	{
		let newAngle = (i+1) * engine.runtime * 360. * 0.016666666667 * 2.0;
		let newAngleRad = newAngle * WEMath.deg2rad;
		orbs[i].origin = new Vec3(
			-Math.sin(newAngleRad) * orbDist, 
			Math.cos(newAngleRad) * orbDist, 
			0);
	}
}
